INSERT INTO `cmf_portal_category` VALUES ('9', '2', '0', '1', '0', '10000', '地标商业中心', '地标商业中心是指在一定区域范围内组织商品流通的枢纽地带。 广义上讲商业中心是指主要行使商业职能的城市；狭义上讲：商业中心是指一个城市商业比较集中的地区。', '0-2-9', '', '', '', 'list', 'article', '{\"thumbnail\":\"portal\\/20181219\\/17777da048ec477975ef6ba3d22c4bfb.jpg\"}');
INSERT INTO `cmf_portal_category` VALUES ('10', '2', '0', '1', '0', '10000', '城市综合体', '随着时代的进步，越来越多源于城市综合体运作模式的综合体建筑不断演化出来，它们的功能比狭义意义上的城市综合体少，根据不同功能的侧重有不同的称号， 但是可以都属于城市综合体。', '0-2-10', '', '', '', 'list', 'article', '{\"thumbnail\":\"portal\\/20181218\\/c019a5a2bdebf3213586d7ad5c516538.jpg\"}');
INSERT INTO `cmf_portal_category` VALUES ('11', '2', '0', '1', '0', '10000', '特色商业街', '商业步行街是步行街中的一种，是城市中商业活动集中的街道。商业步行街是由大量的零售业、服务业商店作为主体，集中于一定的地区，构成有一定长度的街区。', '0-2-11', '', '', '', 'list', 'article', '{\"thumbnail\":\"portal\\/20181218\\/b0e54f582f5390e492e8ef5649047191.jpg\"}');
INSERT INTO `cmf_portal_category` VALUES ('12', '2', '0', '1', '0', '10000', '交通枢纽商业中心', '不同的交通枢纽类型（机场、客运站、高铁、地铁……），在考虑打造商业时会有不同的主导因素。因此，我们会根据其各自的侧重点提供更适宜的方向。不过，交通枢纽型商业也都具备一些共性，或者说面临部分类似的问题。', '0-2-12', '', '', '', 'list', 'article', '{\"thumbnail\":\"portal\\/20181218\\/f5f44f03c70c1ba986fc834671654c76.jpg\"}');
